import React, {useState, useEffect} from 'react'
import { Card, CardContent, Typography, Button } from '@material-ui/core'
import styles from './Track.module.css'
function Track(props) {
    const[track, setTrack] = useState({})
    useEffect(()=>{
        setTrack(props.track)
    }, [props.track, track])
    return (
        <Card className={styles.card} > 
            <CardContent>
                <Typography variant="h5"> {track.artist_name} </Typography>
                <Typography variant='subtitle1'><i className="far fa-play-circle"></i> {track.track_name} </Typography>
                <Typography variant='subtitle1'><i className="fas fa-compact-disc"></i> {track.album_name} </Typography>
                <Button variant="contained" color="primary" > View lyrics</Button>
            </CardContent>
        </Card>
    )
}

export default Track
