import axios from 'axios'

export const fetchTop10 = () => {
    return axios.get(`https://cors-anywhere.herokuapp.com/https://api.musixmatch.com/ws/1.1/chart.tracks.get?chart_name=top&page=1&page_size=10&country=us&f_has_lyrics=1&apikey=${process.env.REACT_APP_MMAPI_KEY}`)
    .then(response => {
        // console.log("Response", response.data.message.body.track_list)
        const tracklist = response.data.message.body.track_list
        return tracklist 
    })
    .catch(error => console.log("Error", error))
}

export const fetchTrackLyrics = (id) => {
    return axios.get(`https://cors-anywhere.herokuapp.com/https://api.musixmatch.com/ws/1.1/track.lyrics.get?track_id=${id}&apikey=${process.env.REACT_APP_MMAPI_KEY}`)
    .then(response => {
        console.log("Response", response)
    })
    .catch(error => console.log("Error", error))
}

export const fetchTrackInfo = (id) => {
    return axios.get(`https://cors-anywhere.herokuapp.com/https://api.musixmatch.com/ws/1.1/track.get?commontrack_id=${id}&apikey=${process.env.REACT_APP_MMAPI_KEY}`)
    .then(response => {
        console.log("Response", response)
    })
    .catch(error => console.log("Error", error))
}
