import React, {useState, useEffect} from 'react'
import axios from 'axios'
import styles from './LyricsPage.module.css'
import {fetchTrackLyrics, fetchTrackInfo} from '../musicxmatchAPI'
function LyricsPage({match}) {
    const[lyrics, setLyrics] = useState({})
    const[trackInfo, setTrackInfo] = useState({})


    useEffect(()=>{
        // const fetchData = async () => {
        //     setLyrics(await fetchTrackLyrics(match.params.id))
        //     setTrackInfo(await fetchTrackInfo(match.params.id))
        // }

        // fetchData()
        axios.get(`https://cors-anywhere.herokuapp.com/https://api.musixmatch.com/ws/1.1/track.get?track_id=${match.params.id}&apikey=${process.env.REACT_APP_MMAPI_KEY}`)
        .then(response => {
            // console.log("ResponseTrackInfo", response.data)
            const trackInfo = response.data.message.body.track
            setLyrics(trackInfo)
        })
        .catch(error => console.log("Error", error))

        console.log("LYRICS", lyrics)
        // console.log("TrackInfo", trackInfo)


    }, [lyrics])
    return (
        <div>
            Lyrics
        </div>
    )
}

export default LyricsPage
