import React, {useState, useEffect} from 'react'
import spinnerGIF from '../../images/spinner.gif'
import styles from './TopTracks.module.css'
import Track from '../Track/Track'
import {fetchTop10} from '../musicxmatchAPI'
import Search from '../Search/Search'

function TopTracks(props) {
    const [tracksList, setTracksList] = useState([])
    const [heading, setHeading] = useState("Top 10 Tracks in US")
    const [retrieved, setRetrieved] = useState(false)
    useEffect(()=>{
        const fetchData = async () => {
            setTracksList(await fetchTop10())
        }
        fetchData()
        // setTracksList(props.trackList)
        // console.log(tracksList)
    }, [])

    const headingHandler = (heading) => {
        setHeading(heading)
    }

    const tracksListHandler = (trackList) => {
        setTracksList(trackList)
    }

    const eventpreventDefault = (event) => {
        event.preventDefault()
    }

    const retrievedTheTracks = (bool) => {
        setRetrieved(bool)
    }

    return (
        <div>
            <h3 align='center' className={styles.heading}>{heading}</h3>
            <Search headingHandler={headingHandler} tracksListHandler={tracksListHandler} eventpreventDefault={eventpreventDefault}/>
            {
                tracksList.length !== 0? (
                    <div className={styles.results}>
                        {
                            tracksList.map((track) => {
                                return <Track track={track.track} key={track.track.track_id}/>
                            })  
                        }

                    </div>
                )
                :  (<img src={spinnerGIF} alt= 'loading....' className={styles.spinner}></img>)

            }
        </div>
    )
}

export default TopTracks
