import React, {useState, useEffect} from 'react'
import {fetchSpecificTrack} from '../musicxmatchAPI'
import {Paper, TextField} from '@material-ui/core'
import styles from './Search.module.css'
function Search(props) {

    const [tracksList, setTracksList] = useState([])
    const [heading, setHeading] = useState("Search Results")
    const [term, setTerm] = useState(null)
    const [event, setEvent] = useState(false)

    // useEffect(()=>{
    //     const fetchData = async () =>{
    //         setTracksList(await fetchSpecificTrack(term))
    //         headingHandler()
    //         tracksListHandler()
    //     }
    //     fetchData()
    // }, [sunmitted])

    const handleSubmit = async () => {
        setTracksList(await fetchSpecificTrack(term))
        console.log("handle Submit Track", tracksList)
        eventpreventDefault()
        headingHandler()
        tracksListHandler()

    }
    const changehandler = (event) => {
        setTerm(event.target.value)
        setEvent(event)

    }

    const headingHandler = () => {
        props.headingHandler(heading)
    }

    const tracksListHandler = () => {
        props.tracksListHandler(tracksList)
    }

    const eventpreventDefault = () => {
        props.eventpreventDefault(event)
    }

    return (
        <div>
        <Paper elevation={8} className = {styles.style}>
            <form onSubmit = {handleSubmit}>
                <TextField autoFocus={true} fullWidth={true} variant='outlined' label='Search' onChange={changehandler}/>
            </form>
        </Paper>
        </div>
    )
}

export default Search
